import { Component } from '@angular/core';
import { ViewProductsComponent } from 'src/app/admin/view-products/view-products.component';
import { ProductMasterService } from '../product-master.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  products:any
  constructor(private pmService:ProductMasterService,){}

  ngOnInit() {
    this.pmService.ViewStock()
      .subscribe(response => {
        this.products = response;
        console.log(response);
      });
}

}
<h1>Product Master Home.</h1>
<br>
<a class="btn btn-info col-sm-2 offset-sm-9 " routerLink="/productmaster/add-product" routerLinkActive="active">+ Add Products</a>



<div class="card">

  
    <img class="card-img-top" src="https://mdbootstrap.com/img/Photos/Others/images/43.webp" alt="Card image cap">
  
   
    <div class="card-body ">
  
  
      <h4 class="card-title"><a>Card title</a></h4>

      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's
        content.</p>
  
      <a href="#" class="btn btn-primary">Button</a>
  
    </div>
  
  </div>
