import { Component } from '@angular/core';
import { CustomerService } from '../customer.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  users:any
  data:any
	constructor(private customerService:CustomerService) { }

	// ngOnInit() {
  //   this.customerService.ViewUserProfile()
  //     .subscribe(response => {
  //       this.users = response;
  //       console.log(response);
  //     });
  ngOnInit(){
    this.customerService.ViewUserProfile().subscribe((res:any)=>{
      this.data = res.data;
      this.users.firstName = this.data.firstName;
      this.users.lastName = this.data.lastName;
      this.users.email = this.data.email;
      this.users.phoneNumber = this.data.phoneNumber;
    })
  }
  handleEdit(form:any)
  {
    console.log(form.value);
    this.customerService.UpdateUserProfile(form.value).subscribe(res =>

      {
        console.log(res);
      });

  }


}


<div class="container rounded bg-white mt-5 mb-5">
  <div class="row">
    <div class="col-md-3 border-right">
      <div class="d-flex flex-column align-items-center text-center p-3 py-5">
        <img
          class="rounded-circle mt-5"
          width="150px"
          src="https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg"
        />
      </div>
    </div>
    <div class="col-md-5 border-right">
      <div class="p-3 py-5">
        <div class="d-flex justify-content-between align-items-center mb-3">
          <h4 class="text-right">Update Profile</h4>
        </div>
        <form #form="ngForm" (ngSubmit)="handleEdit(form)">
          <div class="row mt-2">
            <div class="col-md-6">
              <label class="labels">First Name</label>
              <input
                type="text"
                name="FirstName"
                [(ngModel)]="data.firstName"
                class="form-control"
              />
            </div>
            <div class="col-md-6">
              <label class="labels">Last Name</label>
              <input
                type="text"
                name="lastName"
                [(ngModel)]="data.lastName"
                class="form-control"
                value=""
              />
            </div>
          </div>
          <div class="row mt-3">
            <div class="col-md-12">
              <label class="labels">Email</label>

              <input
                type="text"
                name="email"
                [(ngModel)]="data.email"
                class="form-control"
                value=""
              />
            </div>

            <div class="col-md-12">
              <label class="labels">Phone Number</label>

              <input
                type="text"
                name="phoneNumber"
                [(ngModel)]="data.phoneNumber"
                class="form-control"
                value=""
              />
            </div>
          </div>

          <div class="mt-5 text-center">
            <button class="btn btn-primary profile-button" type="submit">
              Update
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
