import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductMasterService } from '../product-master.service';

@Component({
  selector: 'app-add-product-specification',
  templateUrl: './add-product-specification.component.html',
  styleUrls: ['./add-product-specification.component.css']
})
export class AddProductSpecificationComponent {
  product:any
  pid:any
  constructor(private route:ActivatedRoute,private pmService:ProductMasterService){}


  addProductSpecification(form:any){
    console.log(form.value);
    const id = Number(this.route.snapshot.paramMap.get('id'));
    //console.log(id);
    let data = {
      ...form.value,
      pid: id
    }
    console.log(data.pid);
    this.pmService.AddSpecification(data).subscribe({
        next:(responce)=>{
          console.log(responce);
        }
    })
  }

}
<div class="container">

    <div class="row">

        <form #form="ngForm" (ngSubmit)="addProductSpecification(form);">

            <div>
              <label for="size">Size</label>
                <select ngModel #size="ngModel" name="size">
                  <option>S</option>
                  <option>M</option>
                  <option>L</option>
                  
                </select>

            </div>

              <div>

                <label for="itemQty">Quantity</label>

                <input ngModel #itemQty="ngModel" type="number" name="itemQty" class="form-control">

            </div>

            <div>

                <label for="color">Color</label>

                <input ngModel #color="ngModel" type="text" name="color" class="form-control">

            </div>
            <div>

                <label for="material">Material</label>

                <input ngModel #material="ngModel" type="text" name="material" class="form-control">

            </div>

            <div class="d-grid mt-2">

                <button class="btn btn-primary">Save</button>

            </div>

        </form>

    </div>

</div>
